    private void btnCriarContaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCriarContaActionPerformed
        DecimalFormat number = new DecimalFormat("#,##0.00");
        contaCorrente = new ContaCorrente(Integer.parseInt(txtConta.getText()),Double.parseDouble(txtValor.getText()));
        btnCriarConta.setEnabled(false);
        btnDepositar.setEnabled(true);
        btnSacar.setEnabled(true);
        txtConta.setEnabled(false);
        txtValor.setText("");
        txtSaldo.setText(String.valueOf("R$" + number.format(contaCorrente.getSaldo())));
        txtValor.requestFocus(true);
    }//GEN-LAST:event_btnCriarContaActionPerformed

