    private void btnDepositarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDepositarActionPerformed
        DecimalFormat number = new DecimalFormat("#,##0.00");
        contaCorrente.deposito(Double.parseDouble(txtValor.getText()));
        txtSaldo.setText(String.valueOf("R$" + number.format(contaCorrente.getSaldo())));
        txtValor.setText("");
        txtValor.requestFocus(true);
    }//GEN-LAST:event_btnDepositarActionPerformed

    private void btnSacarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSacarActionPerformed
        DecimalFormat number = new DecimalFormat("#,##0.00");
        if(contaCorrente.getSaldo() < Double.parseDouble(txtValor.getText())){
            JOptionPane.showMessageDialog(null,"Saldo insuficiente!", "Operação inválida!", JOptionPane.ERROR_MESSAGE);
            txtValor.requestFocus(true);
        }else{
            contaCorrente.saque(Double.parseDouble(txtValor.getText()));
            txtSaldo.setText(String.valueOf("R$" + number.format(contaCorrente.getSaldo())));
        }
        txtValor.setText("");
    }//GEN-LAST:event_btnSacarActionPerformed

